#!/bin/sh

cloc --exclude-list-file=bin/cloc_exclude_dirs.txt \
     --by-file \
     --skip-uniqueness \
     --quiet \
     src/sokoenginepy

cloc --exclude-list-file=bin/cloc_exclude_dirs.txt \
     --by-file \
     --skip-uniqueness \
     --quiet \
     --not-match-f="autogenerated.*.py" \
     tests

cloc --exclude-list-file=bin/cloc_exclude_dirs.txt \
     --by-file \
     --skip-uniqueness \
     --quiet \
     src/libsokoengine

cloc --exclude-list-file=bin/cloc_exclude_dirs.txt \
     --list-file=bin/cloc_include.txt \
     --not-match-f="autogenerated.*.py"
